        if i % 2 == 0:
            # Calculate the factorial of i
            fact = 1
            for j in range(1, i+1):
                fact *= j
            result.append(fact)
        else:
            # Calculate the sum of numbers from 1 to i
            s = 0
            for j in range(1, i+1):
                s += j
            result.append(s)
    return result

def histogram(test):
    """
    Given a string representing space separated lowercase letters, return a dictionary of the 
    letter with the most repetition and containing the corresponding count. If several letters 
    have the same occurrence, return all of them.

    Args:
    test (str): a string representing a space separated lowercase letters

    Returns:
    dict: a dictionary of the letter with the most repetition and containing the corresponding count

    Examples:
    >>> histogram('a b c')
    {'a': 1, 'b': 1, 'c': 1}

    >>> histogram('a b b a')
    {'a': 2, 'b': 2}

    >>> histogram('a b c a b')
    {'a': 2, 'b': 2}

    >>> histogram('b b b b a')
    {'b': 4}

    >>> histogram('')
    {}
    """
    if test == '':
        return {}

    letters = test.split()
    letter_count = {}
    max_count = 0

    for letter in letters:
        if letter in letter_count:
            letter_count[letter] += 1
        else:
            letter_count[letter] = 1

        if letter_count[letter] > max_count:
            max_count = letter_count[letter]

    result = {}
    for letter in letter_count:
        if letter_count[letter] == max_count:
            result[letter] = letter_count[letter]

    return result

def get_closest_vowel(word):
    vowels = set("aeiouAEIOU")
    # iterate over the word from the right side
    for i in range(len(word)-2, 0, -1):
        if word[i] in vowels:
            # if a vowel is found, check the characters on the right until the next consonant
            for j in range(i+1, len(word)-1):
                if word[j] not in vowels:
                    # if the character on the right is a consonant, return the vowel
                    return word[i]
            # if no consonant is found on the right, return an empty string
            return ""
    # if no vowel is found in the desired position, return an empty string
    return ""

