def bf(planet1, planet2):
    planets = ["Mercury", "Venus", "Earth", "Mars", "Jupiter", "Saturn", "Uranus", "Neptune"]
    if planet1 not in planets or planet2 not in planets:
        return ()
    p1_idx = planets.index(planet1)
    p2_idx = planets.index(planet2)
    if p1_idx > p2_idx:
        p1_idx, p2_idx = p2_idx, p1_idx
    return tuple([p for p in planets[p1_idx+1:p2_idx] if p != planet1 and p != planet2])

def find_max(words):
    max_word = ""
    max_count = 0
    for word in words:
        unique_chars = set(word)
        if len(unique_chars) > max_count:
            max_word = word
            max_count = len(unique_chars)
        elif len(unique_chars) == max_count and word < max_word:
            max_word = word
    return max_word

def generate_integers(a, b):
    """
    Given two positive integers a and b, return the even digits between a
    and b, in ascending order.

    For example:
    generate_integers(2, 8) => [2, 4, 6, 8]
    generate_integers(8, 2) => [2, 4, 6, 8]
    generate_integers(10, 14) => []
    """

    even_digits = []
    for i in range(a, b+1):
        for digit in str(i):
            if int(digit) % 2 == 0:
                even_digits.append(int(digit))

    return sorted(list(set(even_digits)))
