def bf(planet1, planet2):
    planets = ["Mercury", "Venus", "Earth", "Mars", "Jupiter", "Saturn", "Uranus", "Neptune"]
    if planet1 not in planets or planet2 not in planets:
        return ()
    else:
        index1 = planets.index(planet1)
        index2 = planets.index(planet2)
        if index1 > index2:
            index1, index2 = index2, index1
        return tuple(sorted(planets[index1+1:index2], key=lambda x: planets.index(x)))

def sorted_list_sum(lst):
    # create a new list to store even length strings
    even_lst = []
    for word in lst:
        # check if the length of the word is even
        if len(word) % 2 == 0:
            even_lst.append(word)
    # sort the even length strings by length and then alphabetically
    even_lst.sort(key=lambda x: (len(x), x))
    return even_lst

def x_or_y(n, x, y):
    """A simple program which should return the value of x if n is 
    a prime number and should return the value of y otherwise."""

    if n == 1:
        return y
    elif n == 2:
        return x
    else:
        for i in range(2, n):
            if n % i == 0:
                return y
        return x

def double_the_difference(lst):
    sum_of_squares = 0
    for num in lst:
        if isinstance(num, int) and num >= 0 and num % 2 != 0:
            sum_of_squares += num ** 2
    return sum_of_squares * 2

def compare(game,guess):
    result = []
    for i in range(len(game)):
        if game[i] == guess[i]:
            result.append(0)
        else:
            result.append(abs(game[i] - guess[i]))
    return result

